package cast.index;import java.util.*;import cast.bookManager.*;public class Chapter {	static final private int kMaxSections = 12;		private Section sec[] = new Section[kMaxSections];	private int nSections = 0;		public Chapter() {	}		public int countApplets() {		int nApplets = 0;		for (int i=0 ; i<kMaxSections ; i++)			if (sec[i] != null)				nApplets += sec[i].countApplets();		return nApplets;	}		public int countPages() {		int nPages = 0;		for (int i=0 ; i<kMaxSections ; i++)			if (sec[i] != null)				nPages += sec[i].countPages();		return nPages + 1;	}		protected Section addSection(String dir, String filePrefix, CastEbook castEbook) {		return sec[nSections++] = new Section(dir, filePrefix, castEbook);	}		protected Page addPage(String dir, String filePrefix, int pageIndex, Hashtable indexTable,																					Hashtable dataSetTable, Hashtable indexTranslationTable, CastEbook castEbook) {		return sec[nSections-1].addPage(dir, filePrefix, pageIndex, indexTable, dataSetTable, indexTranslationTable, castEbook);	}}