package axis;import dataView.*;public class TransformDragPosInfo implements PositionInfo {	public TransformDragPosInfo(int transformIndex, int hitOffset) {		this.transformIndex = transformIndex;		this.hitOffset = hitOffset;	}		public TransformDragPosInfo(int transformIndex) {		this.transformIndex = transformIndex;	}		public boolean equals(PositionInfo otherPos) {		if (otherPos == null)			return false;		TransformDragPosInfo other = (TransformDragPosInfo)otherPos;		return (transformIndex == other.transformIndex);	}		public int transformIndex;	public int hitOffset;}