package boxPlotProg;import java.awt.*;import axis.*;import dataView.*;import utils.*;import boxPlot.*;public class DragBox2Applet extends DragBoxApplet {	public void setupApplet() {		DataSet data = new DataSet();		data.addNumVariable("y", getParameter(VAR_NAME_PARAM), getParameter(VALUES_PARAM));				setLayout(new BorderLayout());				NumVariable variable = data.getNumVariable();		XLabel total = new XLabel(translate("No of values") + " = " + Integer.toString(variable.noOfValues()),																								XLabel.CENTER, this);		total.setFont(getStandardBoldFont());		add("North", total);		add("Center", dataPanel(data));		add("South", controlPanel(data));	}		protected DragBoxView createView(DataSet data, HorizAxis theHorizAxis) {		return new DragBox2View(data, this, theHorizAxis);	}}