package cat;import dataView.*;class ConditValueEnumeration implements ValueEnumeration {	private ValueEnumeration mainE, conditE;	private Value conditLabel;	private Value nextMain = null;	private Value nextLabel = null;		public ConditValueEnumeration(Variable mainVar, CatVariable conditVar, Value conditLabel) {		mainE = mainVar.values();		conditE = conditVar.values();		this.conditLabel = conditLabel;		moveToNext();	}		private void moveToNext() {		while (mainE.hasMoreValues() && conditE.hasMoreValues()) {			nextMain = mainE.nextValue();			nextLabel = (Value)conditE.nextValue();			if (nextLabel == conditLabel)				return;		}		nextMain = null;		nextLabel = null;	}		public boolean hasMoreValues() {		return nextMain != null;	}		public Value nextValue() {		Value result = nextMain;		moveToNext();		return result;	}		public RepeatValue nextGroup() {		return new RepeatValue(nextValue(), 1);	}		public double nextDouble() {		return ((NumValue)nextValue()).toDouble();	}}