package contin;import dataView.*;import random.RandomDiscrete;public class BiCatSampleVariable extends CatSampleVariable {	private DataSet data;	private String xKey;		public BiCatSampleVariable(String theName, RandomDiscrete generator, boolean usesGroups,																				DataSet data, String xKey) {		super(theName, generator, usesGroups);		this.data = data;		this.xKey = xKey;	}		public BiCatSampleVariable(String theName, RandomDiscrete generator, DataSet data,																							String xKey) {		this(theName, generator, NO_REPEATS, data, xKey);	}		public void setCounts(int count[]) {		super.setCounts(count);				CatVariable xVar = (CatVariable)data.getVariable(xKey);				int nXCats = xVar.noOfCategories();		int xCount[] = new int[nXCats];		int nYCats = noOfCategories();				for (int i=0 ; i<count.length ; i++)			xCount[i / nYCats] += count[i];				xVar.setCounts(xCount);	}}