package control;import dataView.*;class RangeValueEnumeration extends MeanValueEnumeration {		public RangeValueEnumeration(NumVariable v, int noInGroup, int noOfDecimals) {		super(v, noInGroup, noOfDecimals);	}		static public double evaluateRangeSummary(double vals[]) {		double min = vals[0];		double max = min;		for (int i=1 ; i<vals.length ; i++)			if (vals[i] < min)				min = vals[i];			else if (vals[i] > max)				max = vals[i];		return max - min;	}		public double evaluateSummary(double vals[]) {		return evaluateRangeSummary(vals);	}}public class GroupRangeVariable extends GroupMeanVariable {	public GroupRangeVariable(String theName, NumVariable sourceVariable, int noInGroup, int decimals) {		super(theName, sourceVariable, noInGroup, decimals);	}		public ValueEnumeration values() {		return new RangeValueEnumeration(sourceVariable, noInGroup, decimals);	}		public double evaluateSummary(double vals[]) {		return RangeValueEnumeration.evaluateRangeSummary(vals);	}}