package coreSummaries;import dataView.*;public class MeanVariable extends NumSummaryVariable {	private String sourceKey;	private int decimals;		public MeanVariable(String theName, String sourceKey, int decimals) {		super(theName);		this.sourceKey = sourceKey;		this.decimals = decimals;	}		public void setDecimals(int decimals) {		this.decimals = decimals;	}		protected NumValue evaluateSummary(DataSet sourceData) {		int n = 0;		double sx = 0.0;				NumVariable variable = (NumVariable)sourceData.getVariable(sourceKey);		ValueEnumeration e = variable.values();		while (e.hasMoreValues()) {			double x = e.nextDouble();			if (!Double.isNaN(x)) {				sx += x;				n ++;			}		}		return new NumValue(sx / n, decimals);	}}