package coreVariables;import dataView.*;public class IndexVariable extends NumFunctionVariable {	private int noOfValues;		public IndexVariable(String theName, int noOfValues) {		super(theName);		this.noOfValues = noOfValues;	}		public void setNoOfValues(int noOfValues) {		this.noOfValues = noOfValues;	}		public int getMaxDecimals() {		return 0;	}		public int noOfValues() {		return noOfValues;	}		public Value valueAt(int index) {		return new NumValue(index + 1, 0);	}}