package coreVariables;import dataView.*;public class StackedNumVariable extends NumFunctionVariable {	private NumVariable sourceVar[];	private String[] sourceKey;	private int maxDecimals;		public StackedNumVariable(String theName, DataSet data, String[] sourceKey) {		super(theName);		this.sourceKey = sourceKey;		maxDecimals = 0;		sourceVar = new NumVariable[sourceKey.length];		for (int i=0 ; i<sourceKey.length ; i++) {			sourceVar[i] = (NumVariable)data.getVariable(sourceKey[i]);			maxDecimals = Math.max(maxDecimals, sourceVar[i].getMaxDecimals());		}	}		public int getMaxDecimals() {		return maxDecimals;	}		public int noOfValues() {		int n = 0;		for (int i=0 ; i<sourceVar.length ; i++)			n += sourceVar[i].noOfValues();		return n;	}		public Value valueAt(int index) {		for (int i=0 ; i<sourceVar.length ; i++) {			if (index < sourceVar[i].noOfValues())				return sourceVar[i].valueAt(index);			else				index -= sourceVar[i].noOfValues();		}		return null;	}		public boolean noteVariableChange(String key) {		for (int i=0 ; i<sourceKey.length ; i++)			if (sourceKey[i].equals(key))				return true;		return false;	}}