package coreVariables;import dataView.*;public class SwitchNumVariable extends NumFunctionVariable {	private NumVariable sourceVar[];	private CatVariable switchVar;	private String[] sourceKey;	private String switchKey;	private int maxDecimals;		public SwitchNumVariable(String theName, DataSet data, String[] sourceKey, String switchKey) {		super(theName);		this.sourceKey = sourceKey;		this.switchKey = switchKey;		maxDecimals = 0;		switchVar = (CatVariable)data.getVariable(switchKey);		sourceVar = new NumVariable[sourceKey.length];		for (int i=0 ; i<sourceKey.length ; i++) {			sourceVar[i] = (NumVariable)data.getVariable(sourceKey[i]);			maxDecimals = Math.max(maxDecimals, sourceVar[i].getMaxDecimals());		}	}		public int getMaxDecimals() {		return maxDecimals;	}		public int noOfValues() {		int n = switchVar.noOfValues();		for (int i=0 ; i<sourceVar.length ; i++)			n = Math.min(n, sourceVar[i].noOfValues());		return n;	}		public Value valueAt(int index) {		int catIndex = switchVar.getItemCategory(index);		return sourceVar[catIndex].valueAt(index);	}		public boolean noteVariableChange(String key) {		if (switchKey.equals(key))			return true;		for (int i=0 ; i<sourceKey.length ; i++)			if (sourceKey[i].equals(key))				return true;		return false;	}}