package coreVariables;import dataView.*;public class TotalVariable extends NumFunctionVariable {		private String varKey[];	private NumVariable var[];	private int maxDecimals = 0;		public TotalVariable(String theName, DataSet data, String[] varKey) {		super(theName);		this.varKey = varKey;				var = new NumVariable[varKey.length];		for (int i=0 ; i<varKey.length ; i++) {			var[i] = (NumVariable)data.getVariable(varKey[i]);			maxDecimals = Math.max(maxDecimals, var[i].getMaxDecimals());		}	}		public int getMaxDecimals() {		return maxDecimals;	}		public int noOfValues() {		int nVals = Integer.MAX_VALUE;		for (int i=0 ; i<var.length ; i++)			nVals = Math.min(nVals, var[i].noOfValues());		return nVals;	}		public Value valueAt(int index) {		double value = 0.0;		for (int i=0 ; i<var.length ; i++)			value += var[i].doubleValueAt(index);		return new NumValue(value, maxDecimals);	}		public boolean noteVariableChange(String key) {		for (int i=0 ; i<varKey.length ; i++)			if (varKey[i].equals(key))				return true;		return false;	}}