package dataView;public class FlagEnumeration {	private int index = 0;	private boolean thisFlag;	private int countToGo, totalToGo;		private Flags theFlags;		public FlagEnumeration(Flags theFlags) {		this.theFlags = theFlags;		thisFlag = true;		countToGo = theFlags.counts[0];		totalToGo = theFlags.noOfFlags;	}		public boolean nextFlag() {		if (countToGo == 0) {			index ++;			thisFlag = !thisFlag;			countToGo = theFlags.counts[index];		}				countToGo --;		totalToGo --;		return thisFlag;	}		public boolean hasMoreFlags() {		return totalToGo > 0;	}}