package dataView;import java.awt.*;public class FreqValue extends Value {	public int intValue;	protected int digits;		public FreqValue(int theValue) {		init(theValue);	}		public FreqValue(String valString) throws NullPointerException, NumberFormatException {		if (valString == null)			throw new NullPointerException();		int theValue = Integer.parseInt(valString);		init(theValue);	}		private void init(int theValue) {		intValue = theValue;		int temp = Math.abs(theValue);		digits = (theValue < 0) ? 1 : 0;		while (temp >= 1) {			digits++;			temp /= 10;		}	}	public String toString() {		return String.valueOf(intValue);	}		public void drawAtPoint(Graphics g, int x, int y) {		drawLeft(g, x, y);	}}