package dataView;public class HorizDragPosInfo implements PositionInfo {	public HorizDragPosInfo(int x, int index, int hitOffset) {		this.x = x;		this.index = index;		this.hitOffset = hitOffset;	}		public HorizDragPosInfo(int x) {		this.x = x;	}		public boolean equals(PositionInfo otherPos) {		if (otherPos == null || !(otherPos instanceof HorizDragPosInfo))			return false;		HorizDragPosInfo other = (HorizDragPosInfo)otherPos;		return (x == other.x);	}		public int x;	public int index;	public int hitOffset;}