package dataView;import java.util.*;public class LabelEnumeration implements Enumeration {	private String labelString;	private int charPosition = 0;	private int stringLength;		private void scanWhiteSpace() {		while (charPosition < stringLength && Character.isWhitespace(labelString.charAt(charPosition)))			charPosition++;	}		public LabelEnumeration(String labelString) {		this.labelString = labelString;		stringLength = labelString.length();		scanWhiteSpace();	}		public boolean hasMoreElements() {		return (charPosition < stringLength);	}		public Object nextElement() {		if (charPosition >= stringLength)			throw new NoSuchElementException();		StringBuffer nextString = new StringBuffer(20);		if (labelString.charAt(charPosition) == '#') {			charPosition++;			while (charPosition < stringLength											&& labelString.charAt(charPosition) != '#') {				nextString.append(labelString.charAt(charPosition));				charPosition++;			}			if (charPosition < stringLength											&& labelString.charAt(charPosition) == '#')				charPosition++;		}		else			while (charPosition < stringLength											&& !Character.isWhitespace(labelString.charAt(charPosition))) {				nextString.append(labelString.charAt(charPosition));				charPosition++;			}		scanWhiteSpace();		return nextString.toString();	}		public int countElements() {		int oldCharPosition = charPosition;		int nElements = 0;		while (hasMoreElements()) {			nextElement();			nElements ++;		}		charPosition = oldCharPosition;		return nElements;	}}