package dataView;public class NumCommaValue extends NumValue {		public NumCommaValue(double theValue) {		super(theValue);	}		public NumCommaValue(double theValue, int theDecimals) {		super(theValue, theDecimals);	}		public NumCommaValue(String valString) throws NullPointerException, NumberFormatException {		super(valString);	}		protected ParsedValue parseValue(int drawDecs) {		ParsedValue parsedWithoutCommas = super.parseValue(drawDecs);				ParsedValue theParsedValue = new ParsedValue();		theParsedValue.rightDigits = parsedWithoutCommas.rightDigits;		StringBuffer sb = new StringBuffer(parsedWithoutCommas.leftDigits);		sb.reverse();		for (int index=3 ; index<sb.length() ; index+=4)			sb.insert(index, ',');		sb.reverse();		theParsedValue.leftDigits = sb.toString();				return theParsedValue;	}}