package dataView;abstract public class NumSummaryVariable extends NumVariable implements SummaryVariable {	public NumSummaryVariable(String theName) {		super(theName);	}		public void addSummary(DataSet sourceData) {		addValue(evaluateSummary(sourceData));	}		public void redoLastSummary(DataSet sourceData) {		setValueAt(evaluateSummary(sourceData), noOfValues() - 1);	}		abstract protected NumValue evaluateSummary(DataSet sourceData);		public void clearSummaries() {		clearData();	}}