package dataView;public class ScatterPosInfo implements PositionInfo {	public int x, y, index, xOffset, yOffset;		public ScatterPosInfo(int x, int y, int index, int xOffset, int yOffset) {		this.x = x;		this.y = y;		this.index = index;		this.xOffset = xOffset;		this.yOffset = yOffset;	}		public ScatterPosInfo(int x, int y) {		this.x = x;		this.y = y;	}		public boolean equals(PositionInfo otherPos) {		if (otherPos == null || !(otherPos instanceof ScatterPosInfo))			return false;		ScatterPosInfo other = (ScatterPosInfo)otherPos;		return (x == other.x) && (y == other.y);	}}