package dataView;public class VertDragPosInfo implements PositionInfo {	public VertDragPosInfo(int y, int index, int hitOffset) {		this.y = y;		this.index = index;		this.hitOffset = hitOffset;	}		public VertDragPosInfo(int y) {		this.y = y;	}		public boolean equals(PositionInfo otherPos) {		if (otherPos == null || !(otherPos instanceof VertDragPosInfo))			return false;		VertDragPosInfo other = (VertDragPosInfo)otherPos;		return (y == other.y);	}		public int y;	public int index;	public int hitOffset;}