package distn;import dataView.*;public class ExponDistnVariable extends GammaDistnVariable {		private NumValue lambda = new NumValue(1.0, 0);		public ExponDistnVariable(String theName) {		super(theName);	}		public void setParams(String s) {		if (s == null) {			lambda = new NumValue(1.0, 0);			setScale(1.0);		}		else {			lambda = new NumValue(s);			if (lambda.toDouble() <= 0.0)				System.out.println("Bad exponential parameter, lambda");					setScale(1.0 / lambda.toDouble());		}		double meanVal = getMean().toDouble();		setMinSelection(meanVal);		setMaxSelection(meanVal);	}		public NumValue getLambda() {		return lambda;	}		public void setLambda(NumValue newLambda) {		this.lambda = newLambda;		setScale(1.0 / newLambda.toDouble());	}}