package distn;public class NormalInfo extends DistnInfo {	static final private double kNormalFactor = 1.0 / Math.sqrt(2.0 * Math.PI);	public NormalInfo () {		super();	}		public double zMin() {		return 0.0;	}		public double zMax() {		return 4.0;	}		public double zMode() {		return 0.0;	}		public boolean isSymmetric() {		return true;	}		public double getDensity(double z) {		return kNormalFactor * Math.exp(-0.5 * z * z);	}		public boolean sameParams(ContinDistnVariable v) {		return true;	}}