package distn;public class RectangularInfo extends DistnInfo {		public RectangularInfo () {		super();	}		public double zMin() {		return 0.0;	}		public double zMax() {		return 1.0;	}		public double zMode() {		return 0.5;	}		public boolean isSymmetric() {		return true;	}		public double getDensity(double z) {		return 1.0;	}		public boolean sameParams(ContinDistnVariable v) {		return true;	}}