package distn;public class TInfo extends DistnInfo {	private double factor;	private int df;		public TInfo (int df) {		super();		this.df = df;		factor = Math.exp(GammaDistnVariable.aLoGam(0.5 * (df + 1))										- GammaDistnVariable.aLoGam(0.5)										- GammaDistnVariable.aLoGam(0.5 * df)) / Math.sqrt(df);	}		public double zMin() {		return 0.0;	}		public double zMax() {		return Math.max(4.0, Math.sqrt(df * (Math.pow(0.001, -2.0 / (df + 1)) - 1)));	}		public double zMode() {		return 0.0;	}		public boolean isSymmetric() {		return true;	}		public double getDensity(double z) {		return factor * Math.pow((1.0 + z * z / df), (-0.5 * (df + 1)));	}		public boolean sameParams(ContinDistnVariable v) {		TDistnVariable tv = (TDistnVariable)v;		return tv.getDF() == df;	}}