package distribution;import dataView.*;public class DiscreteDragInfo implements PositionInfo {	public DiscreteDragInfo(int xValue) {		this.xValue = xValue;	}		public boolean equals(PositionInfo otherPos) {		if (otherPos == null || !(otherPos instanceof DiscreteDragInfo))			return false;		DiscreteDragInfo other = (DiscreteDragInfo)otherPos;		return xValue == other.xValue;	}		public int xValue;}