package dotPlotProg;import dataView.*;public class DotPlotXTimeApplet extends DotPlotXApplet {	static final private String START_YEAR_PARAM = "startYear";		protected DataSet readLabelledData() {		DataSet data = new DataSet();		data.addNumVariable("y", getParameter(VAR_NAME_PARAM), getParameter(VALUES_PARAM));		String labelVarName = getParameter(LABEL_NAME_PARAM);		if (labelVarName != null) {			int startYear = Integer.parseInt(getParameter(START_YEAR_PARAM));			int noOfYears = data.getNumVariable().noOfValues();			double year[] = new double[noOfYears];			for (int i=0 ; i<noOfYears; i++)				year[i] = startYear + i;			NumVariable labelVar = new NumVariable(labelVarName);			labelVar.setValues(year);			labelVar.setDecimals(0);			data.addVariable("label", labelVar);		}		return data;	}}