package dragStemLeaf;import java.util.*;import dataView.*;public class StemLeafVariable extends NumVariable {	public StemLeafVariable(String theName) {		super(theName);	}		public void readValues(String valueString) {		StringTokenizer theValues = new StringTokenizer(valueString);		while (theValues.hasMoreTokens())			try {				StemLeafValue nextValue = new StemLeafValue(theValues.nextToken());				addValue(nextValue);			} catch (NumberFormatException e) {				System.err.println("Number ignored (format error)");			}	}		public boolean zeroUnits() {		ValueEnumeration e = values();		while (e.hasMoreValues()) {			StemLeafValue nextVal = (StemLeafValue)e.nextValue();			if (!nextVal.hasZeroUnits())				return false;		}		return true;	}}