package dynamic;import dataView.*;public class NumSeriesVariable extends NumVariable {	private double seriesIndex = 0.0;		public NumSeriesVariable(String theName) {		super(theName);	}		public int seriesLength() {		int len = 0;		ValueEnumeration e = values();		while (e.hasMoreValues()) {			NumSeriesValue val = (NumSeriesValue)e.nextValue();			len = Math.max(len, val.seriesLength());		}		return len;	}		public void setSeriesIndex(double seriesIndex) {		this.seriesIndex = seriesIndex;				ValueEnumeration e = values();		while (e.hasMoreValues()) {			NumSeriesValue val = (NumSeriesValue)e.nextValue();			val.setSeriesIndex(seriesIndex);		}		reSortData();	}		public double getSeriesIndex() {		return seriesIndex;	}		protected Value valFromString(String s) {		return new NumSeriesValue(s);	}		public void setValues(double values[]) {		throw new RuntimeException("Cannot call setValues() for NumSeriesVariable");	}		public void scrollAddValues(double newVal[]) {		throw new RuntimeException("Cannot call scrollAddValues() for NumSeriesVariable");	}}