package dynamicProg;import java.util.*;import dataView.*;import axis.*;import dynamic.*;public class HistogramLagApplet extends HistogramTimeApplet {	static final private String LAG_KEY_PARAM = "lagKey";			protected HistogramView getHistogram(DataSet data, HorizAxis theHorizAxis, VertAxis theVertAxis,												double[] classBoundary, double unitClassWidth, boolean[] groupWithNext) {		StringTokenizer st = new StringTokenizer(getParameter(LAG_KEY_PARAM), "#");		LabelValue lagKey[] = new LabelValue[2];		lagKey[0] = new LabelValue(st.nextToken());		lagKey[1] = new LabelValue(st.nextToken());		HistogramLagView theView = new HistogramLagView(data, this, "y", theHorizAxis, theVertAxis,																					unitClassWidth, classBoundary, groupWithNext, lagKey);		return theView;	}}