package exercise;import java.awt.*;import dataView.*;public class TextItem {	static final private int kImageTextGap = 2;		private String word;	private Image picture;	private int imageWidth, imageAscent;		public TextItem(String word) {		this.word = word;	}		public TextItem(Value value, String extraText) {		this.word = value.toString() + extraText;	}		public TextItem(Image picture, int imageWidth, int imageAscent, String extraText) {		this.picture = picture;		this.imageWidth = imageWidth;		this.imageAscent = imageAscent;		this.word = extraText;	}		public int getWidth(Graphics g) {		int textWidth = 0;		if (word != null)			textWidth = g.getFontMetrics().stringWidth(word);				if (picture == null)			return textWidth;		else			return imageWidth + ((textWidth == 0) ? 0 : (kImageTextGap + textWidth));	}		public void drawItem(Graphics g, int horiz, int baseline, Component c) {		if (picture != null) {			g.drawImage(picture, horiz, baseline - imageAscent, c);			horiz += imageWidth + kImageTextGap;		}		if (word != null)			g.drawString(word, horiz, baseline);	}}