package exercise2;import random.*;public class IntChoice {	private int min, max, value;	private AlmostRandomInteger generator;		public IntChoice(String minString, String maxString, IntChoice oldChoice, ExerciseApplet applet) {		min = Integer.parseInt(minString);		max = Integer.parseInt(maxString);		if (oldChoice != null && oldChoice.min == min && oldChoice.max == max)			generator = oldChoice.generator;		else 			generator = new AlmostRandomInteger(min, max, applet.nextSeed());		value = generator.generateOne();	}		public IntChoice(String valueString) {		value = Integer.parseInt(valueString);	}		public int intValue() {		return value;	}		public String toString() {		return "";	}}