package exercise2;import java.util.*;import dataView.*;abstract public class MeanSDExerciseApplet extends ExerciseApplet {		protected void addTypeDelimiters() {		addType("jointMeanSD", "*");		addType("mean", "*");		addType("sd", "*");	}		protected Object createConstObject(String baseType, String valueString) {		if (baseType.equals("jointMeanSD")) {			StringTokenizer pst = new StringTokenizer(valueString, ",");			NumValue mean = new NumValue(pst.nextToken());			NumValue sd = new NumValue(pst.nextToken());			return new MeanSD(mean, sd);		}		else if (baseType.equals("mean"))			return new NumValue(valueString);		else if (baseType.equals("sd"))			return new NumValue(valueString);		else			return super.createConstObject(baseType, valueString);	}		protected Object createRandomObject(String baseType, String paramString, Object oldParam) {		if (baseType.equals("jointMeanSD"))			return new MeanSD(paramString, getAxisInfo(), new Random(nextSeed()));		else if (baseType.equals("mean"))			return getMean();		else if (baseType.equals("sd"))			return getSD();		else			return super.createRandomObject(baseType, paramString, oldParam);	}		abstract protected NumValue getMean();	abstract protected NumValue getSD();	abstract public String getAxisInfo();}