package exercise2;public class OptionLongTextPanel extends MessagePanel {	private int optionIndex;	private OptionInformation[] optionAnswer;		public OptionLongTextPanel(OptionInformation[] optionAnswer, int optionIndex,																													ExerciseApplet exerciseApplet) {		super(null, exerciseApplet, NO_SCROLL);		this.optionIndex = optionIndex;		this.optionAnswer = optionAnswer;		changeContent();	}		protected void fillContent() {		insertText(optionAnswer[optionIndex].getOptionString());	}}