package exercise2;import utils.*;public class OptionShortTextPanel extends XLabel {	private int optionIndex;	private OptionInformation[] optionAnswer;		public OptionShortTextPanel(OptionInformation[] optionAnswer, int optionIndex,																													ExerciseApplet exerciseApplet) {		super("", XLabel.LEFT, exerciseApplet);		this.optionIndex = optionIndex;		this.optionAnswer = optionAnswer;		changeContent();	}		public void changeContent() {		setText(optionAnswer[optionIndex].getOptionString());	}}