package exerciseSD;import dataView.*;public class CrossHitPosInfo extends ScatterPosInfo {	public int columnIndex, rowIndex;		public CrossHitPosInfo(int x, int y, int columnIndex, int rowIndex, int xOffset, int yOffset) {		super(x, y, -1, xOffset, yOffset);		this.columnIndex = columnIndex;		this.rowIndex = rowIndex;	}		public CrossHitPosInfo(int x, int y) {		super(x, y);	}}