package exper;import dataView.*;public class GroupByYVariable extends CatFunctionVariable {	private NumVariable yVar;	private String yKey;	private int noInLowGroup;		public GroupByYVariable(String theName, DataSet data, String yKey,																			String labelStrings, int noInLowGroup) {		super(theName);		readLabels(labelStrings);		yVar = (NumVariable)data.getVariable(yKey);		this.yKey = yKey;		this.noInLowGroup = noInLowGroup;	}		public int noOfValues() {		return yVar.noOfValues();	}		public Value valueAt(int index) {		int rank = yVar.indexToRank(index);		return getLabel((rank < noInLowGroup) ? 0 : 1);	}		public boolean noteVariableChange(String key) {		return yKey.equals(key);	}}