package experProg;import dataView.*;import coreSummaries.*;public class RandomTreatApplet extends TreatLurkingApplet {		protected void addTreatment (DataSet data) {		RandomisedCatVariable treatVar = new RandomisedCatVariable(getParameter(TREAT_VAR_NAME_PARAM));		treatVar.readLabels(getParameter(TREAT_LABELS_PARAM));		treatVar.readValues(getParameter(TREAT_VALUES_PARAM));		data.addVariable("treat", treatVar);				data.addVariable("biSamp", new BiSampleVariable(data, "error", "treat"));	}		protected SummaryDataSet getSummaryData(DataSet sourceData) {		SummaryDataSet summaryData = new SummaryDataSet(sourceData, "biSamp");					maxDiff = new NumValue(getParameter(MAX_DIFF_PARAM));			DiffSummaryVariable diff = new DiffSummaryVariable(getParameter(EFFECT_NAME_PARAM),																														"y", "treat", maxDiff.decimals);				summaryData.addVariable("est", diff);		summaryData.takeSample();		summaryData.setAccumulate(true);				return summaryData;	}}