package formula;import dataView.*;public class Function extends Bracket {	static final public int LN_FUNCTION = 0;	static final public int EXP_FUNCTION = 1;		static final private String functionName[] = {"ln", "exp"};		private int function;		public Function(FormulaPanel val, int function, FormulaContext context) {		super(val, context);		setLeftLabel(new LabelValue(functionName[function]));		this.function = function;	}		protected double evaluateFormula() {		double baseVal = super.evaluateFormula();		switch (function) {			case LN_FUNCTION:				return Math.log(baseVal);			case EXP_FUNCTION:				return Math.exp(baseVal);		}		return Double.NaN;	}}