package formula;import java.awt.*;public class Picture extends FormulaPanel {	private Image theImage;	private int imageAscent, imageDescent, imageWidth;		public Picture(Image theImage, int width, int ascent, int descent, FormulaContext context) {		super(context);				this.theImage = theImage;		imageAscent = ascent;		imageDescent = descent;		imageWidth = width;	}		public void setImage(Image theImage) {		this.theImage = theImage;	}		protected boolean initialise(Graphics g) {		if (super.initialise(g)) {			layoutWidth = imageWidth;						layoutAscent = imageAscent;			layoutDescent = imageDescent;			return true;		}		else			return false;	}		public void layoutContainer(Container parent) {	}		protected void paintAroundItems(Graphics g) {		g.drawImage(theImage, 0, 0, this);	}		protected double evaluateFormula() {		return Double.NaN;	}}