package formula;import java.awt.*;import dataView.*;public class SampleSize extends FormulaPanel {	private Variable theVar;		public SampleSize(DataSet data, String key, FormulaContext context) {		super(context);				theVar = (Variable)data.getVariable(key);	}		protected boolean initialise(Graphics g) {		if (super.initialise(g)) {			NumValue sampleSize = new NumValue(theVar.noOfValues(), 0);			layoutWidth = sampleSize.stringWidth(g);						layoutAscent = ascent;			layoutDescent = descent;			return true;		}		else			return false;	}		public void layoutContainer(Container parent) {	}		protected void paintAroundItems(Graphics g) {		int horizStart = (getSize().width - layoutWidth) / 2;		int baseline = (getSize().height + layoutAscent - layoutDescent) / 2;				NumValue sampleSize = new NumValue(theVar.noOfValues(), 0);		sampleSize.drawRight(g, horizStart, baseline);	}		protected double evaluateFormula() {		return Double.NaN;	}}