package formula;import java.awt.*;import dataView.*;public class VariableName extends FormulaPanel {	private CoreVariable theVar;		public VariableName(DataSet data, String key, FormulaContext context) {		super(context);				theVar = data.getVariable(key);	}		protected boolean initialise(Graphics g) {		if (super.initialise(g)) {			layoutWidth = g.getFontMetrics().stringWidth(theVar.name);			layoutAscent = ascent;			layoutDescent = descent;			return true;		}		else			return false;	}		public void layoutContainer(Container parent) {	}		protected void paintAroundItems(Graphics g) {		int horizStart = (getSize().width - layoutWidth) / 2;		int baseline = (getSize().height + layoutAscent - layoutDescent) / 2;				g.drawString(theVar.name, horizStart, baseline);	}		protected double evaluateFormula() {		return Double.NaN;	}}