package histo;import java.awt.*;import axis.*;import dotPlot.StackingDotPlotView;public class PlotInfo {	static final public boolean SHOW_HISTO = true;	static final public boolean SHOW_DOTS = false;		static final public int kHideShift = 2000;		public HorizAxis horizAxis;	public DensityAxis densityAxis;	public HistoView theHisto;	public StackingDotPlotView theDotPlot;	public boolean showHisto;		public PlotInfo(HorizAxis horizAxis, DensityAxis densityAxis, HistoView theHisto,										StackingDotPlotView theDotPlot, boolean showHisto) {		this.horizAxis = horizAxis;		this.densityAxis = densityAxis;		this.theHisto = theHisto;		this.theDotPlot = theDotPlot;		this.showHisto = showHisto;	}		public void changeDisplayType(boolean showHisto) {		this.showHisto = showHisto;		Point histoLocation = theHisto.getLocation();		Point dotLocation = theDotPlot.getLocation();		if (showHisto) {			if (histoLocation.y < 0)				theHisto.setLocation(histoLocation.x, histoLocation.y + kHideShift);			if (dotLocation.y >= 0)				theDotPlot.setLocation(dotLocation.x, dotLocation.y - kHideShift);		}		else {			if (histoLocation.y >= 0)				theHisto.setLocation(histoLocation.x, histoLocation.y - kHideShift);			if (dotLocation.y < 0)				theDotPlot.setLocation(dotLocation.x, dotLocation.y + kHideShift);		}	}}