package histoProg;import java.awt.*;import dataView.*;import histo.*;public class FreqPolyGroupApplet extends FreqPolyApplet {	static final private String COUNTS2_PARAM = "counts2";		protected DataView createHistoPoly(DataSet data) {		int[] group1Counts = readCounts(COUNTS_PARAM);		int[] group2Counts = readCounts(COUNTS2_PARAM);				FreqPolyGroupView histoPoly = new FreqPolyGroupView(data, this, group1Counts, group2Counts);		histoPoly.lockBackground(Color.white);		return histoPoly;	}}