package imageGroups;import java.awt.*;public class TickCrossImages extends AudioVisual {	static public Image cross, tick, question;		static public boolean loadedCrossAndTick = false;		synchronized static public void loadCrossAndTick(Component theComponent) {		if (loadedCrossAndTick)			return;				MediaTracker tracker = new MediaTracker(theComponent);				cross = loadImage("wrong.png", tracker, cross, theComponent);		tick = loadImage("correct.png", tracker, tick, theComponent);		question = loadImage("question.png", tracker, question, theComponent);				waitForLoad(tracker);		loadedCrossAndTick = true;	}}