package imageGroups;import java.awt.*;public class ValueAdjusterImages extends AudioVisual {	static public Image leftArrow, boldLeftArrow, dimLeftArrow, rightArrow, boldRightArrow,											dimRightArrow, valueBackground, dimValueBackground;		static public boolean loadedValueAdjuster = false;		synchronized static public void loadValueAdjuster(Component theComponent) {		if (loadedValueAdjuster)			return;				MediaTracker tracker = new MediaTracker(theComponent);				leftArrow = loadImage("valueAdjust/leftArrow.gif", tracker, leftArrow, theComponent);		boldLeftArrow = loadImage("valueAdjust/boldLeftArrow.gif", tracker, boldLeftArrow, theComponent);		dimLeftArrow = loadImage("valueAdjust/dimLeftArrow.gif", tracker, dimLeftArrow, theComponent);		rightArrow = loadImage("valueAdjust/rightArrow.gif", tracker, rightArrow, theComponent);		boldRightArrow = loadImage("valueAdjust/boldRightArrow.gif", tracker, boldRightArrow, theComponent);		dimRightArrow = loadImage("valueAdjust/dimRightArrow.gif", tracker, dimRightArrow, theComponent);		valueBackground = loadImage("valueAdjust/valueBackground.gif", tracker, valueBackground, theComponent);		dimValueBackground = loadImage("valueAdjust/dimValueBackground.gif", tracker, dimValueBackground, theComponent);				waitForLoad(tracker);		loadedValueAdjuster = true;	}}