package inference;import dataView.*;import distn.*;public class TTableDataSet extends DataSet {	static final private int kMaxDF = 100;	static final private int kTDecimals = 3;		private double pLess;		public TTableDataSet(double pLess) {		double df[] = new double[kMaxDF + 1];		double t[] = new double[kMaxDF + 1];		for (int i=1 ; i<=kMaxDF ; i++) {			df[i] = i;			t[i] = TTable.quantile(pLess, df[i]);		}		NumVariable dfVar = new NumVariable("degrees of freedom =");		dfVar.setValues(df);		dfVar.setDecimals(0);		addVariable("df", dfVar);				NumVariable tVar = new NumVariable("t-value =");		tVar.setValues(t);		tVar.setDecimals(kTDecimals);		addVariable("t", tVar);				this.pLess = pLess;	}		public void selectDF(int df) {		if (df <= 0)			clearSelection();		else if (df <= kMaxDF)			setSelection(df);		else {			NumVariable dfVar = (NumVariable) getVariable("df");			NumVariable tVar = (NumVariable) getVariable("t");			((NumValue)dfVar.valueAt(0)).setValue(df);			((NumValue)tVar.valueAt(0)).setValue(TTable.quantile(pLess, df));			clearSelection();			setSelection(0);		}	}}