package linMod;import dataView.*;public class SlopeInterceptVariable extends NumSummaryVariable {	static final public int INTERCEPT = 0;	static final public int SLOPE = 1;		private int paramType;	private String sourceXKey, sourceYKey;	private int decimals;		public SlopeInterceptVariable(String theName, String sourceXKey, String sourceYKey,																		int decimals, int paramType) {		super(theName);		this.sourceXKey = sourceXKey;		this.sourceYKey = sourceYKey;		this.decimals = decimals;		this.paramType = paramType;	}		protected NumValue evaluateSummary(DataSet sourceData) {		LSEstimate lse = new LSEstimate(sourceData, sourceXKey, sourceYKey);		double param = (paramType == INTERCEPT) ? lse.getIntercept() : lse.getSlope();		return new NumValue(param, decimals);	}}