package loess;import dataView.*;public class DurbinWatsonValueVariable extends NumSummaryVariable {	static final public int kDecimals = 3;		private String residKey;		static public NumValue durbWatsonFromResid(NumVariable resid) {		ValueEnumeration e = resid.values();				double x0 = Double.NaN;		double x1 = e.nextDouble();		double sxx = x1 * x1;		double sdd = 0.0;				while (e.hasMoreValues()) {			x0 = x1;			x1 = e.nextDouble();			sxx += x1 * x1;			sdd += (x0 - x1) * (x0 - x1);		}				return new NumValue(sdd / sxx, kDecimals);	}		public DurbinWatsonValueVariable(String theName, String residKey) {		super(theName);		this.residKey = residKey;	}		protected NumValue evaluateSummary(DataSet sourceData) {		NumVariable resid = (NumVariable)sourceData.getVariable(residKey);		return durbWatsonFromResid(resid);	}}