package loess;import dataView.*;import models.*;public class ResidLogLogValueVariable extends ResidValueVariable {	static final private double kLog10Factor = 1.0 / Math.log(10.0);	public ResidLogLogValueVariable(String theName, DataSet data, String oneXKey, String yKey,																			String lineKey, int decimals) {		super(theName, data, oneXKey, yKey, lineKey, decimals);	}		public Value valueAt(int index) {		NumValue x = (NumValue)((NumVariable)data.getVariable(xKey[0])).valueAt(index);		NumValue y = (NumValue)((NumVariable)data.getVariable(yKey)).valueAt(index);		LinearModel theModel = (LinearModel)data.getVariable(lineKey);		double fitVal = theModel.evaluateMean(x.toDouble());		double resid = Math.log(y.toDouble() / fitVal) * kLog10Factor;		NumValue residVal = new NumValue(resid, decimals);		return residVal;	}}