package loess;import java.util.*;public class TransAxisLabelInfo {	private int powerIndex[];	private int stepIndex[];		public TransAxisLabelInfo(String transformStepString) {		StringTokenizer st = new StringTokenizer(transformStepString);		int noOfSteps = st.countTokens();		powerIndex = new int[noOfSteps];		stepIndex = new int[noOfSteps];		st = new StringTokenizer(transformStepString, " (,)");		for (int i=0 ; i<noOfSteps ; i++) {			powerIndex[i] = Integer.parseInt(st.nextToken());			stepIndex[i] = Integer.parseInt(st.nextToken());		}	}		public TransAxisLabelEnumeration getLabelEnumeration(double minPower,																		double maxPower, int currentPowerIndex) {		return new TransAxisLabelEnumeration(powerIndex, stepIndex, minPower, maxPower,																									currentPowerIndex);	}}