package matrix;import java.awt.*;import dataView.*;public class MatrixFormulaValue extends MatrixFormulaPanel {	private CoreMatrixView matrix;		public MatrixFormulaValue(CoreMatrixView matrix, XApplet applet) {		this.matrix = matrix;		add(matrix);	}		public void setGroupKey(String groupKey) {		matrix.setGroupKey(groupKey);	}		protected void doInitialisation(Graphics g) {		super.doInitialisation(g);				Dimension dim = matrix.getMinimumSize();		layoutWidth = dim.width;		layoutHeight = dim.height;				layoutHeadingHeight = matrix.getHeadingHeight(g);	}		public void layoutContainer(Container parent) {		initialise(getGraphics());				matrix.setBounds((parent.getSize().width - layoutWidth) / 2, 0, layoutWidth, Math.min(parent.getSize().height, layoutHeight));	}		protected void paintAroundItems(Graphics g) {	}}